			let s = "Zach has been streaming for ";
			if (d.years  > 0) s += `${d.years} years, `;
			if (d.months > 0) s += `${d.months} months, `;
			if (d.weeks  > 0) s += `${d.weeks} weeks, `;
			if (d.days   > 0) s += `${d.days} days.`;
